{
  "mcpServers": {
    "filesystem": {
      "command": "bunx",
      "args": [
        "@modelcontextprotocol/server-filesystem",
        "./frontend/src",
        "./backend/src",
        "./shared/types"
      ],
      "description": "Direct filesystem access for reading and writing project files"
    },
    "github": {
      "command": "bunx",
      "args": ["-y", "@modelcontextprotocol/server-github"],
      "env": {
        "GITHUB_PERSONAL_ACCESS_TOKEN": "${GITHUB_TOKEN}"
      },
      "description": "GitHub integration for repository management and collaboration"
    },
    "postgres": {
      "command": "bunx",
      "args": [
        "@modelcontextprotocol/server-postgres",
        "${DATABASE_URL}"
      ],
      "description": "PostgreSQL database operations and queries"
    },
    "puppeteer": {
      "command": "bunx",
      "args": ["-y", "@modelcontextprotocol/server-puppeteer"],
      "description": "Browser automation for testing and web scraping"
    },
    "fetch": {
      "command": "bunx",
      "args": ["-y", "@modelcontextprotocol/server-fetch"],
      "description": "HTTP requests and API interactions"
    },
    "context7": {
      "command": "bunx",
      "args": ["@context7/mcp-server"],
      "env": {},
      "description": "Context7 MCP Server for enhanced context management"
    },
    "task-master": {
      "command": "bunx",
      "args": ["@task-master/mcp-server"],
      "env": {},
      "description": "Task Master for project and task management"
    },
    "exa": {
      "command": "bunx",
      "args": ["@exa/mcp-server"],
      "env": {
        "EXA_API_KEY": "${EXA_API_KEY}"
      },
      "description": "Exa MCP Server for advanced search capabilities"
    },
    "knowledge-graph-memory": {
      "command": "bunx",
      "args": ["@knowledge-graph/mcp-server"],
      "env": {},
      "description": "Knowledge Graph Memory for persistent context and relationships"
    },
    "magic-ui": {
      "command": "bunx",
      "args": ["@magic-ui/mcp-server"],
      "env": {},
      "description": "Magic UI MCP for UI component generation and manipulation"
    }
  },
  "defaults": {
    "packageManager": "bun",
    "commandPrefix": "bunx"
  },
  "hooks": {
    "preInstall": "echo 'Using Bun for package management'",
    "postInstall": "bun install --frozen-lockfile"
  },
  "guidance": {
    "preferBun": true,
    "npmCommands": {
      "npm install": "bun install",
      "npm run": "bun run",
      "npx": "bunx",
      "npm init": "bun init",
      "npm test": "bun test"
    }
  }
}

